pragma solidity >=0.4.21 < 0.6.0;

contract SimpleToken {

    //create an array with all balances
    mapping (address => uint256) public balances;
    address public owner;
    uint public initialSupply = 5400000;

    // log the transfer coin from one address to another
    event TransferCoin(address indexed _from, address indexed _to, uint amount);

    constructor () public payable {
        //require(msg.value == 350 ether, "350 (5.4 million tokens) ether initial funding required");
        /* set the owner to the creator of this contract */
        balances[tx.origin] = initialSupply;
    }

    // send coin from account to account and return sender balance
    function transferCoin(address reciever, uint amount) public returns (bool sufficient) {
        // If the amount is more than what user is trying to send, return sufficient = false
        if (balances[msg.sender] < amount) return false;
        // else deduct amount from sender, and increment it to reciever's account
        else {
            balances[msg.sender] -= amount;
            balances[reciever] += amount;
            emit TransferCoin(msg.sender, reciever, amount);
            getBalanceInEth(msg.sender);
            return true;// sufficient = true, transaction is complete
        }
    }

    function convert(uint amount, uint conversionRate) public pure returns (uint convertedVal) {
        // divide the amount of ether to the conversion rate to get token value
        return amount / conversionRate;
    }

    // show account balance
    function getBalance(address addr) public view returns (uint256) {
        return balances[addr];
    }

    function getBalanceInEth(address acc) public view returns (uint) {
        //  convert tokens to eth by dividing each ether by 15400
        return convert(getBalance(acc),15400);
    }
}
